import React, { useEffect } from "react";
import MOMENT from "moment";
import Link from 'next/link';
import { useRouter } from 'next/router';

import { AsyncTypeahead } from "@Components";
import { initGA, logPageView } from '@Services/analytics';
import { EntityUpdateProps } from "./EntityUpdate";

const EntityUpdate: React.FunctionComponent<EntityUpdateProps> = ({
    children,
    leftNav,
    containerClass = "container",
    gridSize = {
        left: 'col-md-2',
        mid: 'col-md-8',
        right: 'col-md-4'
    }
}): JSX.Element => {

    useEffect(() => { /* tslint:disable-next-line */
        $(function () { /* tslint:disable-next-line */
            ($('[data-toggle="tooltip"]') as any).tooltip()
        });
    });

    const entityUpdateAllowed = MOMENT().diff(MOMENT(cinData.updated_at), 'days') > 7;

    return <div className="mt-3 ml-1">
        <button 
            disabled={!entityUpdateAllowed}
            data-toggle="tooltip" 
            data-placement="right" 
            title = {
                entityUpdateAllowed ? 
                "Click here to raise an updation request" : 
                "Information is already upto date, cannot raise request"
            }
            type="button" 
            className="btn btn-secondary btn-sm btn-block">
            Request Data Updation
        </button>
        <div className="mt-2">Info last updated on </div>
        <div>{MOMENT(cinData.updated_at).format("MMMM Do, YYYY")}</div>
    </div>;
};

export { EntityUpdate };
