// #region Global Imports
import * as React from "react";
import { NextPage, GetServerSideProps } from "next";
import Link from 'next/link';
import { useSelector, useDispatch } from "react-redux";
import { Http } from '../../src/Services/API/Http';


import PageTitle from "./../components/common/PageTitle";
import UserDetails from "../components/user-profile-lite/UserDetails";
import TopReferrals from "./../components/common/TopReferrals";

import {
    Container, Row, Col, Card,
    CardHeader,
    CardBody,
    ListGroup,
    ListGroupItem,
} from "shards-react";
// #endregion Global Imports

// #region Local Imports
import { withTranslation } from "@Server/i18n";
import {
    // Container,
    Top,
    TopText,
    Middle,
    MiddleLeft,
    MiddleLeftButtons,
    MiddleRight,
    Apod,
    ApodButton,
} from "@Styled/Home";
import { IStore } from "@Redux/IStore";
import { HomeActions } from "@Actions";
import { Heading, LocaleButton } from "@Components";
// #endregion Local Imports

// #region Interface Imports
import { IHomePage, ReduxNextPageContext } from "@Interfaces";
// #endregion Interface Imports

const Home: NextPage<IHomePage.IProps, IHomePage.InitialProps> = ({
    t,
    i18n,
    cinData,
}) => {

    const home = useSelector((state: IStore) => state.home);
    const dispatch = useDispatch();

    // remove later
    const details = cinData; 
    const refData = [
        {
          title: "CIN",
          value: details["CIN"]
        },
        {
          title: "Company Name",
          value: details["Company Name"]
        },
        {
          title: "ROC Code",
          value: details["ROC Code"]
        },
        {
          title: "Registration Number",
          value: details["Registration Number"]
        },
        {
          title: "Company Category",
          value: details["Company Category"]
        },
        {
          title: "Company SubCategory",
          value: details["Company SubCategory"]
        },
        {
          title: "Class of Company",
          value: details["Class of Company"]
        },
        {
          title: "Authorised Capital(Rs)",
          value: details["Authorised Capital(Rs)"]
        },
        {
          title: "Paid up Capital(Rs)",
          value: details["Paid up Capital(Rs)"]
        },
        {
          title: "Number of Members(Applicable in case of company without Share Capital)",
          value: details["Number of Members(Applicable in case of company without Share Capital)"]
        },
        {
          title: "Date of Incorporation",
          value: details["Date of Incorporation"]
        },
        {
          title: "Registered Address",
          value: details["Registered Address"]
        },
        {
          title: "Address",
          value: details["Address other than R/o where all or any books of account and papers are maintained"]
        },
        {
          title: "Email Id",
          value: details["Email Id"]
        },
        {
          title: "Whether Listed or not",
          value: details["Whether Listed or not"]
        },
        {
          title: "Suspended at stock exchange",
          value: details["Suspended at stock exchange"]
        },
        {
          title: "Date of last AGM",
          value: details["Date of last AGM"]
        },
        {
          title: "Date of Balance Sheet",
          value: details["Date of Balance Sheet"]
        },
        {
          title: "Company Status(for efiling)",
          value: details["Company Status(for efiling)"]
        }
      ];

    const renderLocaleButtons = (activeLanguage: string) =>
        ["en", "es", "tr"].map(lang => (
            <LocaleButton
                key={lang}
                lang={lang}
                isActive={activeLanguage === lang}
                onClick={() => i18n.changeLanguage(lang)}
            />
        ));

    return (
        <Container fluid className="main-content-container px-4">
            {/* Page Header */}
            <Row noGutters className="page-header py-4">
                PageTitle
          {/* <PageTitle title={details['Company Name']} subtitle="Company" className="text-sm-left mb-3" /> */}
            </Row>

            <Row>
                <Col lg="6" md="12" sm="12" className="mb-4">

                    <Card small>
                        <CardHeader className="border-bottom">
                            <h6 className="m-0">{title}</h6>
                            <div className="block-handle" />
                        </CardHeader>

                        <CardBody className="p-0">
                            <ListGroup small flush className="list-group-small">
                                {referralData.map((item, idx) => (
                                    <ListGroupItem key={idx} className="d-flex px-3">
                                        <span className="text-semibold text-fiord-blue">{item.title}</span>
                                        <span className="ml-auto text-right text-semibold text-reagent-gray">
                                            {item.value}
                                        </span>
                                    </ListGroupItem>
                                ))}
                            </ListGroup>
                        </CardBody>
                    </Card>

                    <TopReferrals
                        title={"Details"}
                        referralData={this.getTopReferralsData(details)}
                    />
                </Col>

                {/* Users Overview */}
                <Col lg="4" md="8" sm="12" className="mb-4 sticky100">
                    <UserDetails
                        userDetails={{
                            name: details['Company Name'],
                            avatar: require("../images/avatars/0.jpg"),
                            jobTitle: details["Company Status(for efiling)"],
                            performanceReportTitle: "Workload",
                            performanceReportValue: 74,
                            metaTitle: "Description",
                            metaValue:
                                `${details['Company Name']} incorporated with MCA on ${details['Date of Incorporation']}. ${details['Company Name']} is listed in the class of pvtltd company and classified as Non Govt Company. This company is registered at Registrar of Companies(ROC), ${details["ROC Code"]} with an Authorized Share Capital of Rs. ${details["Authorised Capital(Rs)"]} and its paid up capital is Rs. ${details["Paid up Capital(Rs)"]}.`
                        }}
                    />
                </Col>
            </Row>

            <Row>
                <Col lg="6" md="12" sm="12" className="mb-4">
                    <div className="card card-small mb-4">
                        <div className="card-header border-bottom">
                            <h6 className="m-0">Directors</h6>
                        </div>
                        <div className="card-body p-0 pb-3 text-center">
                            <table className="table mb-0">
                                <thead className="bg-light">
                                    <tr className="text-semibold text-fiord-blue">
                                        <th scope="col" className="border-0">#</th>
                                        <th scope="col" className="border-0">DIN/PAN</th>
                                        <th scope="col" className="border-0">Name</th>
                                        <th scope="col" className="border-0">Begin date</th>
                                        <th scope="col" className="border-0">End date</th>
                                        <th scope="col" className="border-0">Surrendered DIN</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        details.dins.map((director, index) => {
                                            return (
                                                <tr key={`director_${director[0]}`} className="text-semibold text-reagent-gray">
                                                    <td>{index + 1}</td>
                                                    <td>{director[0]}</td>
                                                    <td>{director[6]}</td>
                                                    <td>{director[7]}</td>
                                                    <td>{director[8]}</td>
                                                    <td>{director[6]}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </Col>
            </Row>

            <Row>
                <Col lg="6" md="12" sm="12" className="mb-4">
                    <div className="card card-small mb-4">
                        <div className="card-header border-bottom">
                            <h6 className="m-0">Charges</h6>
                        </div>
                        <div className="card-body p-0 pb-3 text-center">
                            <table className="table mb-0">
                                <thead className="bg-light">
                                    <tr className="text-semibold text-fiord-blue">
                                        <th scope="col" className="border-0">#</th>
                                        <th scope="col" className="border-0">Assets under charge</th>
                                        <th scope="col" className="border-0">Charge Amount</th>
                                        <th scope="col" className="border-0">Date of Creation</th>
                                        <th scope="col" className="border-0">Date of Modification</th>
                                        <th scope="col" className="border-0">Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        details.charges.map((charge, index) => {
                                            return (
                                                <tr key={`charge_${charge[1]}`} className="text-semibold text-reagent-gray">
                                                    <td>{index + 1}</td>
                                                    <td>{charge[0]}</td>
                                                    <td>{charge[1]}</td>
                                                    <td>{charge[2]}</td>
                                                    <td>{charge[3]}</td>
                                                    <td>{charge[4]}</td>
                                                </tr>
                                            );
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </Col>
            </Row>
        </Container>
    );
};

Home.getInitialProps = async (
    ctx: ReduxNextPageContext
): Promise<IHomePage.InitialProps> => {
    const { cin } = ctx.query;
    const cinData = await Http.Request('GET', `http://3.7.5.125:4000/v1/company/${cin}`);
    await ctx.store.dispatch(
        HomeActions.GetApod({
            params: { hd: true },
        })
    );
    return {
        namespacesRequired: ["common"],
        cinData,
        name: "pushkar"
    };
};

// export const getServerSideProps: GetServerSideProps = async (context) => {
//     const cinData = await Http.Request('GET', `http://3.7.5.125:4000/v1/company/U55209AP2018PTC107672`);
//     console.log("cinData on server side: ", cinData);
//     return {
//       props: {
//         cinData,
//         name: "pushkar"
//       }
//     }
// }


const Extended = withTranslation("common")(Home);


export default Extended;
