import React, { useState } from "react";
import { AsyncTypeahead } from 'react-bootstrap-typeahead';
import Link from 'next/link';

import { IAsyncTypeahead } from "./AsyncTypeahead";
import { Http } from '../../Services/API/Http';

const SEARCH_URI = 'https://kitaablu.com/api/v1/search/';
const CustomAsyncTypeahead: React.FunctionComponent<IAsyncTypeahead.IProps> = (): JSX.Element => {
    const [isLoading, setIsLoading] = useState(false);
    const [options, setOptions] = useState([]);

    const handleSearch = async (query) => {
        setIsLoading(true);

        try {
            const options: any = await Http.Request(
                'GET',
                `${SEARCH_URI + query}`,
                {}
            );

            setOptions(options);
        } catch (error) {
            console.log("Error while fetching props: ", error);
        }

        setIsLoading(false);
    };

    return (
        <AsyncTypeahead
            id="async-example"
            isLoading={isLoading}
            labelKey="name"
            minLength={3}
            onSearch={handleSearch}
            options={options}
            placeholder="Search using company name ..."
            renderMenuItemChildren={(option, props) => {
                return (
                    <Link href="/company/[cin]" as={`/company/${option.CIN}`}>
                        <div>
                            <a>{option.name}</a>
                        </div>
                    </Link>
                )
            }}
            onChange={(options) => {
                console.log('fire options: ', options);
            }}
        />
    );
};

export { CustomAsyncTypeahead as AsyncTypeahead };
