// #region Global Imports
import React, { useEffect } from "react";
import MOMENT from "moment";
import _ from "lodash";
import { NextPage, GetServerSideProps } from "next";
import { NextSeo } from 'next-seo';
import Link from 'next/link';
import { useSelector, useDispatch } from "react-redux";
import { Http } from '../../src/Services/API/Http';
// #endregion Global Imports

// #region Local Imports
import { withTranslation } from "@Server/i18n";
import { Layout, EntityUpdate } from "@Components";
// #endregion Local Imports

// #region Interface Imports
import { IHomePage, ReduxNextPageContext } from "@Interfaces";
// #endregion Interface Imports

const Home: NextPage<IHomePage.IProps, IHomePage.InitialProps> = ({
    t,
    data
}) => {
    if (!data) {
        return (
            <Layout>
                <h1 className="my-4">
                    <div>NO DATA FOUND FOR IFSC.</div>
                </h1>
            </Layout>
        );
    }

    const refData = [
        {
            "title": "BANK",
            "value": data["bank"]
        },
        {
            "title": "BRANCH",
            "value": data["branch"]
        },
        {
            "title": "IFSC",
            "value": data["ifsc"]
        },
        {
            "title": "MICR CODE",
            "value": data["micr"]
        },
        {
            "title": "CONTACT",
            "value": data["contact"]
        },
        {
            "title": "ADDRESS",
            "value": data["address"]
        },
        {
            "title": "STD CODE",
            "value": data["std_code"]
        },
        {
            "title": "CITY",
            "value": data["city"]
        },
        {
            "title": "DISTRICT",
            "value": data["district"]
        },
        {
            "title": "STATE",
            "value": data["state"]
        }
    ];

    const companyName = data["bank"];
    const SEO = {
        title: `${data["bank"]}, ${data["branch"]} ${data["city"]} ${data["state"]} ${data["ifsc"]} - IFSC Code, MICR Code and Full Address`,
        description: `${data["bank"]}, ${data["branch"]} ${data["city"]} ${data["state"]}, IFSC Code: ${data["ifsc"]}, MICR Code: ${data["micr"]}, Address: ${data["address"]}. Find all Indian bank branches for NEFT, RTGS and ECS transactions.`
    };

    return (
        <Layout
            containerClass = "container-fluid"
            gridSize = {{
                left: 'col-md-2',
                mid: 'col-lg-7 col-md-8',
                right: 'col-lg-3 col-md-4'
            }}
            leftNav = {
                <div className="sticky-top" style={{ top: '100px' }}>
                    <EntityUpdate 
                        lastUpdatedAt = {data.updated_at}
                        entityType = "1"
                        entityId = {data["CIN"]}
                    />
                </div>
            }
        >
            <NextSeo
                title={SEO.title}
                description={SEO.description}
            />
            <h1 className="my-4">
                <div>{data["bank"]}</div>
                <small>IFSC - {data["ifsc"]}</small>
            </h1>
            <div className="card mb-4 shadow bg-white rounded" id="intro">
                <div className="card-body">
                    <div>
                        {companyName} is a {data["Class of Company"]} incorporated on {data["Date of Incorporation"]}. It is classified as {data["Company SubCategory"]} and is registered at Registrar of Companies, {data["ROC Code"]}. It's authorized share capital is INR {data["Authorised Capital(Rs)"]} and paid up capital is INR {data["Paid up Capital(Rs)"]}. It is a {data["Whether Listed or not"]} company. 
                    </div>
                    <div>
                        Corporate Identification Number(CIN) of {companyName} is {data["CIN"]} and it's registration number is {data["Registration Number"]}. Company can be contacted on email - {data["Email Id"]} and registered address - {data["Registered Address"]}. Current status of {companyName} is - {data["Company Status(for efiling)"] || data["LLP Status"]}.
                    </div>
                    <div>
                        Director data of {companyName} can be found <a href="#directors">Here</a>.
                    </div>
                </div>
            </div>

            <h4 id="basicInfo">Information</h4>
            <div className="card mb-4 shadow bg-white rounded" id="basicInfo">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-hover">
                            <tbody>
                                {
                                    refData.filter(data => data.value).map(data => {
                                        return (
                                            <tr key={data.title}>
                                                <th scope="row">{data.title}</th>
                                                <td>{data.value}</td>
                                            </tr>
                                        )
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </Layout>
    );
};

Home.getInitialProps = async (
    ctx: ReduxNextPageContext
): Promise<IHomePage.InitialProps> => {
    const { ifsc } = ctx.query;

    const props = {
        data: null
    };

    try {
        // props.data = await Http.Request('GET', `https://kitaablu.com/api/v1/ifsc/${ifsc}`);
        props.data = await Http.Request('GET', `http://localhost:4000/api/v1/ifsc/${ifsc}`);
    } catch (error) {
        console.log("Error while fetching data for ifsc: ", ifsc, error);
    }

    return props;
};

const Extended = withTranslation("common")(Home);
export default Extended;
