// #region Global Imports
import * as React from "react";
import { NextPage } from "next";
import Link from 'next/link';
import { useSelector, useDispatch } from "react-redux";
// #endregion Global Imports

// #region Local Imports
import { withTranslation } from "@Server/i18n";
import { IStore } from "@Redux/IStore";
import { HomeActions } from "@Actions";
import { LocaleButton, Layout } from "@Components";
import { Http } from '../../src/Services/API/Http';

import { ICompanyPage, ReduxNextPageContext } from "@Interfaces";

const Home: NextPage<ICompanyPage.IProps, ICompanyPage.InitialProps> = ({
    t,
    i18n,
    bannerDetails,
    companies
}) => {
    const home = useSelector((state: IStore) => state.home);
    const dispatch = useDispatch();


    const renderLocaleButtons = (activeLanguage: string) =>
        ["en", "es", "tr"].map(lang => (
            <LocaleButton
                key={lang}
                lang={lang}
                isActive={activeLanguage === lang}
                onClick={() => i18n.changeLanguage(lang)}
            />
        ));

    return (
        <Layout>
            <h1 className="my-4">
                <small>List of companies registered in last 3 days</small>
            </h1>

            <div className="card mb-4">
                <h2 className="card-header">List Of Companies</h2>
                <div className="card-body">
                    {
                        companies.length ? (
                            <div className="table-responsive">
                                <table className="table table-hover">
                                    <thead>
                                        <tr className="text-semibold text-fiord-blue">
                                            <th scope="col">#</th>
                                            <th scope="col">CIN/FCRN</th>
                                            <th scope="col">Company Name</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            companies.map((company, index) => {
                                                return (
                                                    <tr key={`director_${company.CIN}`} className="text-semibold text-reagent-gray">
                                                        <td>{index + 1}</td>
                                                        <td>
                                                            <Link href="/company/[cin]" as={`/company/${company.CIN}`}>
                                                                <a>{company.CIN}</a>
                                                            </Link>
                                                        </td>
                                                        <td>{company.company_name}</td>
                                                    </tr>
                                                );
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        ) :
                        <div>
                            No Company registered
                        </div>
                    }
                </div>
            </div>

            <div className="card mb-4">
                <div className="card-body">
                    <table className="table table-hover">
                        <thead>
                            <tr className="text-semibold text-fiord-blue">
                                <th scope="col">CIN</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                companies.map(company => {
                                    return (
                                        <tr key={`company_${company.CIN}`} className="text-semibold text-reagent-gray">
                                            <td>
                                                <Link href="/company/[cin]" as={`/company/${company.CIN}`}>
                                                    <a>{company.CIN}</a>
                                                </Link>
                                            </td>
                                        </tr>
                                    );
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>

        </Layout>
    );
};

Home.getInitialProps = async (
    ctx: ReduxNextPageContext
): Promise<ICompanyPage.InitialProps> => {
    let response = {
        bannerDetails: null,
        companies: []
    };

    try {
        response.bannerDetails = await Http.Request('GET', `https://kitaablu.com/api/v1/company/banner`);
        response.companies = await Http.Request('GET', `https://kitaablu.com/api/v1/company?doiDayDiff=3`);
    } catch (error) {
        console.log("Error while fetching props: ", error);
    }

    return response;
};

const Extended = withTranslation("common")(Home);
export default Extended;
